import React, { useState } from 'react';
import './App.css';
import OnScreenKeyboard from './OnScreenKeyboard';

function App() {
  const [text, setText] = useState("");

  const handleKeyPress = (key) => {
    setText((prev) => prev + key);
  };

  const handleChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Submitted suggestion: ${text}`);
    setText("");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Suggestion Window</h2>
        <form onSubmit={handleSubmit} style={{ width: '100%', maxWidth: 400 }}>
          <textarea
            value={text}
            onChange={handleChange}
            rows={6}
            style={{ width: '100%', fontSize: '1.1rem', marginBottom: 12 }}
            placeholder="Type your suggestion..."
          />
          <OnScreenKeyboard onKeyPress={handleKeyPress} />
          <button type="submit" style={{ padding: '10px 24px', fontSize: '1rem', marginTop: 8 }}>
            Submit
          </button>
        </form>
      </header>
    </div>
  );
}

export default App;
