import React, { useState, useRef } from 'react';
import './App.css';
import OnScreenKeyboard from './OnScreenKeyboard';

function App() {
  const [text, setText] = useState("");
  const [drawing, setDrawing] = useState(false);
  const canvasRef = useRef(null);

  const handleKeyPress = (key) => {
    setText((prev) => prev + key);
  };

  const handleChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Submitted suggestion: ${text}`);
    setText("");
    clearCanvas();
  };

  // Canvas drawing handlers
  const startDrawing = (e) => {
    setDrawing(true);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.beginPath();
    const { x, y } = getCursorPosition(e, canvas);
    ctx.moveTo(x, y);
  };

  const draw = (e) => {
    if (!drawing) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { x, y } = getCursorPosition(e, canvas);
    ctx.lineTo(x, y);
    ctx.strokeStyle = "#222";
    ctx.lineWidth = 2;
    ctx.lineCap = "round";
    ctx.stroke();
  };

  const stopDrawing = () => {
    setDrawing(false);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  // Helper to get mouse/touch position
  const getCursorPosition = (e, canvas) => {
    const rect = canvas.getBoundingClientRect();
    let clientX, clientY;
    if (e.touches) {
      clientX = e.touches[0].clientX;
      clientY = e.touches[0].clientY;
    } else {
      clientX = e.clientX;
      clientY = e.clientY;
    }
    return {
      x: clientX - rect.left,
      y: clientY - rect.top
    };
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Suggestion Window</h2>
        <form onSubmit={handleSubmit} style={{ width: '100%', maxWidth: 400 }}>
          <div style={{ marginBottom: 16 }}>
            <canvas
              ref={canvasRef}
              width={380}
              height={180}
              style={{
                border: '2px solid #aaa',
                borderRadius: 8,
                background: '#fff',
                touchAction: 'none'
              }}
              onMouseDown={startDrawing}
              onMouseMove={draw}
              onMouseUp={stopDrawing}
              onMouseLeave={stopDrawing}
              onTouchStart={startDrawing}
              onTouchMove={draw}
              onTouchEnd={stopDrawing}
            />
            <div style={{ marginTop: 8 }}>
              <button type="button" onClick={clearCanvas} style={{ fontSize: 14, padding: '4px 12px' }}>
                Clear Canvas
              </button>
            </div>
          </div>
          <textarea
            value={text}
            onChange={handleChange}
            rows={8}
            style={{ width: '100%', fontSize: '1.1rem', marginBottom: 12, minHeight: 150 }}
            placeholder="Type your suggestion..."
          />
          <OnScreenKeyboard onKeyPress={handleKeyPress} />
          <button type="submit" style={{ padding: '10px 24px', fontSize: '1rem', marginTop: 8 }}>
            Submit
          </button>
        </form>
      </header>
    </div>
  );
}

export default App;
