import React, { useState, useRef } from 'react';
import './App.css';

function App() {
  const [drawing, setDrawing] = useState(false);
  const canvasRef = useRef(null);

  // Canvas drawing handlers
  const startDrawing = (e) => {
    setDrawing(true);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.beginPath();
    const { x, y } = getCursorPosition(e, canvas);
    ctx.moveTo(x, y);
  };

  const draw = (e) => {
    if (!drawing) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { x, y } = getCursorPosition(e, canvas);
    ctx.lineTo(x, y);
    ctx.strokeStyle = "#222";
    ctx.lineWidth = 2;
    ctx.lineCap = "round";
    ctx.stroke();
  };

  const stopDrawing = () => {
    setDrawing(false);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  // Helper to get mouse/touch position
  const getCursorPosition = (e, canvas) => {
    const rect = canvas.getBoundingClientRect();
    let clientX, clientY;
    if (e.touches) {
      clientX = e.touches[0].clientX;
      clientY = e.touches[0].clientY;
    } else {
      clientX = e.clientX;
      clientY = e.clientY;
    }
    return {
      x: clientX - rect.left,
      y: clientY - rect.top
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Handwritten suggestion submitted!');
    clearCanvas();
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Suggestion Window</h2>
        <div
          style={{
            width: 600, // Increased from 400 to 600
            background: '#fff',
            borderRadius: 16, // Slightly larger border radius
            boxShadow: '0 4px 24px rgba(0,0,0,0.12)', // Slightly stronger shadow
            padding: 36, // Increased padding
            margin: '0 auto'
          }}
        >
          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: 24 }}>
              <canvas
                ref={canvasRef}
                width={540} // Increased from 360 to 540
                height={300} // Increased from 180 to 300
                style={{
                  border: '2px solid #aaa',
                  borderRadius: 12, // Larger border radius
                  background: '#f9f9f9',
                  touchAction: 'none'
                }}
                onMouseDown={startDrawing}
                onMouseMove={draw}
                onMouseUp={stopDrawing}
                onMouseLeave={stopDrawing}
                onTouchStart={startDrawing}
                onTouchMove={draw}
                onTouchEnd={stopDrawing}
              />
              <div style={{ marginTop: 12, textAlign: 'right' }}>
                <button
                  type="button"
                  onClick={clearCanvas}
                  style={{
                    fontSize: 16,
                    padding: '6px 18px',
                    background: '#eee',
                    border: 'none',
                    borderRadius: 6,
                    cursor: 'pointer'
                  }}
                >
                  Clear
                </button>
              </div>
            </div>
            <button
              type="submit"
              style={{
                width: '100%',
                padding: '16px 0',
                fontSize: '1.2rem',
                background: '#1976d2',
                color: '#fff',
                border: 'none',
                borderRadius: 8,
                cursor: 'pointer'
              }}
            >
              Send
            </button>
          </form>
        </div>
      </header>
    </div>
  );
}

export default App;
